import { ComponentFixture, TestBed } from '@angular/core/testing';
import { of } from 'rxjs';
import { VehiculosComponente } from './vehiculos.component';

const mockVehiculos = [
  { id: 1, marca: 'McLaren', linea: 'MP4-12C', modelo: 2020, kilometraje: 10000, color: 'Naranja', imagen: 'https://hips.hearstapps.com/es.h-cdn.co/cades/contenidos/7122/i4_0.jpg' },
  { id: 2, marca: 'Chevrolet', linea: 'Corvette', modelo: 2022, kilometraje: 20000, color: 'Rojo', imagen: 'https://autos93.com/cdn/shop/files/F721DFBB-AAC8-4C75-8DD1-A227840455E5_540x.jpg?v=1747062318'},
  { id: 3, marca: 'Nissan', linea: 'March', modelo: 2019, kilometraje: 20000, color: 'Rojo', imagen: ''},
];

describe('VehiculosComponente', () => {
  let fixture: ComponentFixture<VehiculosComponente>;

  beforeEach(async () => {
    await TestBed.configureTestingModule({
      imports: [VehiculosComponente], // componente standalone
    }).compileComponents();

    fixture = TestBed.createComponent(VehiculosComponente);

    // ⬇️ Inyecta los datos directamente en las props del componente
    fixture.componentInstance.vehiculos$ = of(mockVehiculos);
    // (No es necesario setear total$ ni porMarcaLista$ para esta prueba)
  });

  it('debe renderizar una tabla con 3 filas de vehículos más el encabezado', async () => {
    // 1) Dispara el render
    fixture.detectChanges();

    // 2) Espera a que el async pipe resuelva y Angular pinte
    await fixture.whenStable();
    await fixture.whenRenderingDone();

    // 3) Render final
    fixture.detectChanges();

    const host: HTMLElement = fixture.nativeElement as HTMLElement;

    const table = host.querySelector('table');
    // console.log(host.innerHTML); // <- Debug opcional
    expect(table).not.toBeNull();

    const bodyRows = host.querySelectorAll('tbody tr');
    expect(bodyRows.length).toBe(3);

    const headerCells = host.querySelectorAll('thead tr th');
    expect(headerCells.length).toBe(4); // #, Marca, Línea, Modelo
  });
});
