import { ComponentFixture, TestBed, waitForAsync } from '@angular/core/testing';
import { of } from 'rxjs';
import { By } from '@angular/platform-browser';
import { VehiculosComponente } from './vehiculos.component';
import { VehiculoService } from './service/vehiculo-service.service';

const mockVehiculos = [
  { id: 1, marca: 'Renault', linea: 'Kangoo', modelo: 2017 },
  { id: 2, marca: 'Chevrolet', linea: 'Spark', modelo: 2018 },
  { id: 3, marca: 'Nissan', linea: 'March', modelo: 2019 },
];

describe('VehiculosComponente', () => {
  let fixture: ComponentFixture<VehiculosComponente>;

  const vehiculoServiceMock = {
    getVehiculos: () => of(mockVehiculos),
  };

  beforeEach(waitForAsync(async () => {
    await TestBed.configureTestingModule({
      imports: [VehiculosComponente], // componente standalone
      providers: [{ provide: VehiculoService, useValue: vehiculoServiceMock }],
    }).compileComponents();
  }));

  beforeEach(() => {
    fixture = TestBed.createComponent(VehiculosComponente);
  });

  it('debe renderizar una tabla con 3 filas de vehículos más el encabezado', waitForAsync(async () => {
    fixture.detectChanges();
    await fixture.whenStable();
    fixture.detectChanges();
    const table = fixture.debugElement.query(By.css('table'));
    expect(table).toBeTruthy();
    const bodyRows = fixture.debugElement.queryAll(By.css('tbody tr'));
    expect(bodyRows.length).toBe(3);
    const headerCells = fixture.debugElement.queryAll(By.css('thead tr th'));
    expect(headerCells.length).toBe(4);
  }));
});
